mapper.select:
select
p.*,
s.name as s_name
from contact_plan p
inner join stu s on s.sid=p.sid
where 1=1
 and p.status = #{status} 
 and p.sid = #{sid} 
 and s.cc = #{eid} 
 and p.plan_date >= #{startDate} 
 and p.plan_date <= #{endDate} 
 and p.plan_date = #{planDate,jdbcType=DATE} 
 and s.name like CONCAT('%',#{sName},'%')
 and s.node = #{node} 
order by p.plan_date asc
