mapper.select:
select
c.*,
s.name as s_name,
s.tel as tel,
course.type_name as course_type_name
from course_schedule c inner join stu s on s.sid=c.sid
inner join course_type course on course.pkid=c.course_type_id
where 1=1
 and s.cc=#{eid} 
 and s.node=#{nid} 
 and c.notify_status = #{notifyStatus} 

and c.booking_date >= #{startDate}
and c.booking_date <= #{endDate}

  and c.booking_date >= date_add(curdate(),interval 1 day)
  and c.booking_date < date_add(curdate(),interval 2 day)

select
c.*,
s.name as sName,
s.tel as tel
from course_schedule c inner join stu s on s.sid=c.sid
where 1=1
 and s.cc=#{eid} 
 and s.node=#{nid} 
 and c.trace_status = #{traceStatus} 


  and c.booking_date >= #{startDate}
  and c.booking_date <= #{endDate}



  and (
    (c.booking_date >= curdate() and c.booking_date < date_add(curdate(),interval 1 day))
    or (c.booking_date < curdate() and c.trace_status='PST01')
  )

order by c.booking_date asc
