mapper.select:
select
  e.eid as eid,
  count(s.sid) as num
from
  emp_info e left outer join stu s on s.cc=e.eid
  and s.create_date >= #{startDate}
  and s.create_date <= #{endDate}

  e.nid=#{nid}

group by e.eid
order by e.eid

select e.eid as eid, count(c.course_type_id) as num from emp_info e left outer join stu s on s.cc=e.eid left outer join course_schedule c on s.sid=c.sid and c.course_type_id=1 and c.booking_date >= #{startDate} and c.booking_date <= #{endDate}
e.nid=#{nid}
group by e.eid order by e.eid

select e.eid as eid, count(c.course_type_id) as num from emp_info e left outer join stu s on s.cc=e.eid left outer join course_schedule c on s.sid=c.sid and c.course_type_id=1 and c.sign_status='SIN01' and c.booking_date >= #{startDate} and c.booking_date <= #{endDate}
e.nid=#{nid}
group by e.eid order by e.eid

select e.eid as eid, count(c.course_type_id) as num from emp_info e left outer join course_schedule c on e.eid=c.close_eid and c.course_type_id=1 and c.sign_status='SIN01' and c.booking_date >= #{startDate} and c.booking_date <= #{endDate}
e.nid=#{nid}
group by e.eid order by e.eid

select e.eid as eid, count(s.course_type_id) as num from emp_info e left outer join stu_course s on s.eid=e.eid and s.course_type_id=2 and s.create_date >= #{startDate} and s.create_date <= #{endDate}
e.nid=#{nid}
group by e.eid order by e.eid

select e.eid as eid, count(c.course_type_id) as num from emp_info e left outer join stu s on s.cc=e.eid left outer join stu_course c on s.sid=c.sid and c.course_type_id=2 and c.create_date >= #{startDate} and c.create_date <= #{endDate}
e.nid=#{nid}
group by e.eid order by e.eid
