mapper.select:
select * from stu where 1=1
 and (name like CONCAT('%',#{name},'%') or niki_name like CONCAT('%',#{name},'%')) 
 and status = #{status} 
 and type = #{type} 
 and verify_status = #{verifyStatus} 
 and cc = #{cc} 
 and node = #{node}

select
*
from stu s
where s.cc=#{fromCc}

  and s.pre_cc=#{preCc}



    exists (
      select * from contact_plan p where p.sid=s.sid and p.status='PST01'
      and p.plan_date >= #{startDate}
      and p.plan_date <= #{endDate}
    )


    or exists (
      select * from course_schedule c where c.sid=s.sid and c.sign_status='SIG02'
      and c.booking_date >= #{startDate}
      and c.booking_date <= #{endDate}
    )
